package Assignment;

public class swappingValue {

	
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int a, b, c;
		a = 30;
		b = 20;
		System.out.println("Before swapping number is: a ="+a+", b="+b);
		
		c= a;
		a=b;
		c=c;
		System.out.println("After swapping number is: a ="+a+", b= "+b);
	}
	
}
