package array;

public class AssigningArray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		//array declaration
			int arr[];

			
		//assigning memory to array
			arr = new int[10];
			
			
		//assigning elements
			arr[1] = 100;
			arr[2] = 22;
			arr[8] = 34;
			arr[5] = 233;
		//accessing array elements
			for(int i= 0; i<10; i++) {
			
			System.out.println(arr[i]);	
			}
	}

}
